#!/bin/sh
ln -s -f /Library/Frameworks/Awesomium.framework .
export LIB=./lib
export DYLD_LIBRARY_PATH=$LIB/macosxuniversal
export JAVA_HOME=`/usr/libexec/java_home -v '1.6*'`
#JAVA=/Library/Java/JavaVirtualMachines/jdk1.7.0_17.jdk/Contents/Home/bin/java
JAVA=java 
#JNI_OPTS=-Xcheck:jni -Xverbose:jni
$JAVA -server $JNI_OPTS -XX:MaxGCPauseMillis=2000 -XX:GCTimeRatio=20 -XX:+UseParallelGC -XX:+UseParallelOldGC -Xrunjdwp:transport=dt_socket,address=8000,server=y,suspend=n -Djogl.stage.2d.flatness=.05 -Df3.ma.debug.links=false -Df3.assimp.convert.quat.to.euler=false -Df3.assimp.disable.deformers=false -Df3.jbullet.debug.physics=true -Df3.jbox2d.debug.physics=true -Df3.jbox2d.disable.threads=false -Df3.jbox2d.enable.bayazit=true -Df3.awesomium.jogl.enable.subimage=true -Djnlp.jogl.debug.Texture=true -Df3.jogl.stage.disable.supersample=true -Df3.jogl.stage.disable.scissor=true -Df3.jogl.stage.debug.scissor=false -Df3.debug.cursor=false -Df3.jogl.stage.debug.draw=false -Df3.jogl.stage.debug.lights=false -Df3.jogl.stage.debug.meshes=false -Dapple.awt.graphics.UseQuartz=true -Df3.jogl.stage.enable.textured.text=false -Df3.debug.anim=false -d32 -Xmx1G -Xbootclasspath/p:lib/jsr166.jar -classpath ${LIB}/jbullet.jar:${LIB}/vecmath.jar:${LIB}/org.f3.jbullet.jar:${LIB}/paranamer-2.6.jar:${LIB}/f3c.jar:${LIB}/js.jar:${LIB}/jogl-all.jar:${LIB}/gluegen-rt.jar:${LIB}/jogl-cg.jar:${LIB}/f3.svg.awt.jar:${LIB}/f3.xhtml.awt.jar:${LIB}/core-renderer.jar:${LIB}/f3.awesomium.jogl.jar:${LIB}/f3.cg.jogl.awt.jar:${LIB}/org.f3.media.video.corevideo.jar:build/expr.jar:${LIB}/json-simple-1.1.1.jar:${LIB}/guava-16.0.1.jar:${LIB}/org.f3.media.audio.impl.fmod.jar:${LIB}/f3.assimp.jar:${LIB}/jassimp.jar:${LIB}/jbox2d.jar:${LIB}/org.f3.jbox2d.jar:${LIB}/reflections-0.9.9-RC1-uberjar.jar:${LIB}/commons-imaging-1.0-SNAPSHOT.jar:${LIB}/com.realityinteractive.imageio.tga.jar org.f3.runtime.Main expr.Main $*